import './App.css';
import { useState } from 'react';

function App() {
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  return (
    <>
      <h1>Stopwatch</h1>
      <p>{time}</p>
      <button onClick={() => setIsRunning(!isRunning)}>
        {isRunning ? 'Stop' : 'Start'}
      </button>
      <button onClick={() => setTime(0)}>Reset</button>
    </>
  );
}

export default App;
